import React, { useState } from 'react';
import './App.css';

function App() {
  const [display, setDisplay] = useState('0'); // State to hold the display value
  const [currentNumber, setCurrentNumber] = useState(''); // State to hold the current number being input
  const [operator, setOperator] = useState(null); // State to hold the current operator
  const [previousNumber, setPreviousNumber] = useState(null); // State to hold the previous number entered

  // Function to handle when a number button is clicked
  const handleNumberClick = (num) => {
    // Concatenate the clicked number to the current number being input
    setCurrentNumber(currentNumber === '0' ? num : currentNumber + num);
    if (display === '0') {
      setDisplay(num);
    } else {
      setDisplay(display + num);
    }
  };

  // Function to handle when an operator button is clicked
  const handleOperatorClick = (op) => {
    setOperator(op);
    setPreviousNumber(currentNumber);
    setCurrentNumber('');
  };

  // Function to handle when the equals button is clicked
  const handleEqualsClick = () => {
    const num1 = parseFloat(previousNumber);
    const num2 = parseFloat(currentNumber);
    let result;
    switch (operator) {
      case '+':
        result = num1 + num2;
        break;
      case '-':
        result = num1 - num2;
        break;
      case '*':
        result = num1 * num2;
        break;
      case '/':
        result = num1 / num2;
        break;
      default:
        return;
    }
    setDisplay(result.toString());
    setCurrentNumber(result.toString());
    setOperator(null);
  };

  // Function to handle when the clear button is clicked
  const handleClearClick = () => {
    setDisplay('0');
    setCurrentNumber('');
    setOperator(null);
    setPreviousNumber(null);
  };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        <button onClick={() => handleNumberClick('7')}>7</button>
        <button onClick={() => handleNumberClick('8')}>8</button>
        <button onClick={() => handleNumberClick('9')}>9</button>
        <button onClick={() => handleOperatorClick('/')}>/</button>

        <button onClick={() => handleNumberClick('4')}>4</button>
        <button onClick={() => handleNumberClick('5')}>5</button>
        <button onClick={() => handleNumberClick('6')}>6</button>
        <button onClick={() => handleOperatorClick('*')}>*</button>

        <button onClick={() => handleNumberClick('1')}>1</button>
        <button onClick={() => handleNumberClick('2')}>2</button>
        <button onClick={() => handleNumberClick('3')}>3</button>
        <button onClick={() => handleOperatorClick('-')}>-</button>

        <button onClick={() => handleNumberClick('0')}>0</button>
        <button onClick={handleClearClick}>C</button>
        <button onClick={handleEqualsClick}>=</button>
        <button onClick={() => handleOperatorClick('+')}>+</button>
      </div>
    </div>
  );
}

export default App;
